import React,{Component} from 'react';
import Time from '../lib/Time'
class ReactTimer extends Component {
    constructor(props){
        super(props)
        this.state = {
            time:0,
            status:false
        }
    }
    InputHandler = (event) =>
    {

        const seconds = parseInt(event.target.value);
        if (seconds && typeof seconds === 'number')
        {this.setState({
            time:seconds,
        })}
        else {
            this.setState(() => ({ time: 0 }));
        }

    }
    startHandler = () =>
    {
        if(this.state.status!=='start')
          {
            setInterval(() => 
            {
                if(this.state.time!==0&&this.state.time>-1)
                {
                    this.setState((prevState) => ({time:prevState.time -10}))
                }
                else
                {
                    this.setState({
                        time:0
                    })
                    clearInterval(this.interval);
                }
            },10)
            this.setState({
                status:'start'
            });
          }
    }
    stopTimer() {
        if (this.state.status && this.state.status === 'started') {

            clearInterval(this.interval);

            this.setState((prevState) => {
                return ({
                    status: 'stopped',
                });
            });
        }
    }
    resetHandler = () =>
    {
        clearInterval(this.interval);

        this.setState(() => ({ status: null, time: 0 }));
    }
    
    
    render(){
       const time = new Time();
    return (
        <div>
        <input type = "text" value = {this.state.time} onChange = {this.InputHandler} />
        {time.getTime(this.state.time)}
        <button onClick = {this.startHandler}>start</button>
        <button onClick = {this.resetHandler}>Reset</button>
        <button onClick = {this.stopTimer}>Reset</button>
        </div>
        )
}
}

export default ReactTimer
