import React,{Component} from 'react';
import Time from '../lib/Time'
class ReactTimer extends Component {
    constructor(props){
        super(props)
        this.state = {
            time:0,
            status:false
        }
    }
    InputHandler = (event) =>
    {
        this.setState({
            time:event.target.value
        })

    }
    startHandler = () =>
    {
        setInterval(() => {
            if((!this.state.time)&&!this.state.status)
            {
               this.setState(prevState => ({ time: prevState.time - 10 }))
            }
            else
            {
               this.setState({
                time:0,
                status:false
               })
            }
        },10)
    }
    

    resetHandler = () =>
    {
       this.setState({
        time:0,
        status:false
       })
    }
    
    render(){
       const times = new Time();
    return (
        <div>
        <input type = "text" value = {this.state.time} onChange = {this.InputHandler} />
        {times.getTime(this.state.time)}
        <button onClick = {this.startHandler}>start</button>
        <button onClick = {this.resetHandler}>Reset</button>
        </div>
        )
}
}

export default ReactTimer
