import React,{Component} from 'react';

class Message extends Component {
	constructor(props){

        super(props)
        this.state={
          counter:this.props.number,
          essage:'Welcome',
          date : new Date()
        }
        this.decrement = this.decrement.bind(this)
    }
    increment(){
        this.setState({
            counter : this.state.counter + this.props.number,
            date:new Date(),
            essage :'App1'
        })
    }
    decrement = () => {
        this.increment()
    }
  
     render(){
         return (
         	<div>
            <h1>
              {this.state.essage} {this.state.counter}<br/>
              date : {this.state.date.toLocaleTimeString()}
               
            </h1>

            <button onClick ={this.decrement}>Subscribe </button>
            </div>
         	)
     }

}
export default Message
