import React,{Component} from 'react';
import Time from '../lib/Time'
class ReactTimer extends Component {
    constructor(props){
        super(props)
        this.state = {
            time:0,
            status:false
        }
    }
    InputHandler = (event) =>
    {
        if(this.state.time!==0&& typeof this.state.time === 'number')
        {   this.setState({
                time:(event.target.value)*1000
            })
        }
        
    }
    startHandler = () =>
    {
          if(this.state.status!=='start')
          {
            setInterval(() => 
            {
                if(this.state.time!==0)
                {
                    this.setState((prevState) => ({time:prevState.time -10}))
                }
                else
                {
                    this.setState({
                        time:0
                    })
                    clearInterval(this.interval);
                }
            },10)
            this.setState({
                status:'start'
            });
          }
    }
    resetHandler = () =>
    {
        clearInterval(this.interval);
        this.setState({
            time:0
        })
      
    }
    
    render(){
       const time = new Time();
    return (
        <div>
        <input type = "text" value = {this.state.time} onChange = {this.InputHandler} />
        {time.getTime(this.state.time)}
        <button onClick = {this.startHandler}>start</button>
        <button onClick = {this.resetHandler}>Reset</button>
        </div>
        )
}
}

export default ReactTimer
