import React,{Component} from 'react';
import Time from '../lib/Time'

class AdvancedTimer extends Component {
    constructor(props){
        super(props)
        this.state = {
            time:0,
            hrs:0,
            min:0,
            sec:0,
            timeUnit:null,
            status:null,
            ready:false
        }
    }
    componentWillUnmount() {
        clearInterval(this.interval);
    }
    onFocusChange = (Unit) =>
    {
          
          this.setState({
            timeUnit:Unit
          })
    }
    formatTime(time) {
        time = parseInt(time);
        return time < 10 ? '0' + time : time.toString().slice(time.toString().length - 2);
    }
    InputHandler = (time) =>
    {
        
        console.log(this.state.timeUnit)
        if(time&&this.state.timeUnit==='h')
        {
            if(time<0)
            {
                this.setState({
                    hrs:'00'
                })
            }
             else
                {
                    this.setState((prevState) => {
                        
                         let hrs = parseInt(this.formatTime(prevState.hrs + time));
                         console.log(hrs)
                         return ({ hrs: this.formatTime(hrs) });
            })

        }
        }
         else if(time&&this.state.timeUnit==='s')
        {
             if(time<0)
            {
                this.setState({
                    hrs:'00'
                })
            }
             else
                {
                    this.setState((prevState) => {
                         
                         let hrs = parseInt(this.formatTime(prevState.sec + time));
                         if(hrs>59)
                         {
                            hrs = prevState.sec
                         }
                         return ({ sec: this.formatTime(hrs),
                                                      });
            })

        }
        }
        else if (time&&this.state.timeUnit==='m') 
        {
             if(time<0)
            {
                this.setState({
                    hrs:'00'
                })
            }
             else
                {
                    this.setState((prevState) => {
                         let hrs = parseInt(this.formatTime(prevState.min + time));
                         if(hrs>59)
                         {
                            hrs = prevState.min
                         }
                         return ({ min: this.formatTime(hrs) });
            })

        }
        }
       if(this.state.status!=='started'&&(this.state.hrs>0||this.state.min>0||this.state.sec>0))
       {
        this.setState({
            ready:true
        })
       }


    }

    startHandler = () =>
    {
        if(this.state.ready)
        {
           this.setState({
            time : (this.state.hrs*3600+this.state.min*60+this.state.sec)*1000
           })
        }

        if(this.state.status!=='started' &&this.state.time>0){
            this.interval = setInterval(() => 
            {
                if(this.state.time!==0&&this.state.time>0)
                {
                    this.setState((prevState) => ({time:prevState.time -10}))
                }
                else
                {
                    this.setState({
                        time:0
                    })
                    clearInterval(this.interval);
                }
            },10)
            this.setState({
                status:'started'
            });
          }
    }
    stopHandler = () => {
        console.log(this.state.time)
        if (this.state.status && this.state.status === 'started') {

            clearInterval(this.interval);
            
            this.setState((prevState) => {
                return ({
                    status: 'stopped',
                });
            });
        }
    }
    resetHandler = () =>
    {
        clearInterval(this.interval);

        this.setState(() => ({ status: null, time: 0,hrs:'00',sec:'00',min:'00' }));
    }
    resumeHandler =() =>
    {
        if((this.state.status === 'stopped'))
        {
            this.interval = setInterval(() => 
            {
                if(this.state.time!==0&&this.state.time>0)
                {
                    this.setState((prevState) => ({time:prevState.time -10}))
                }
                else
                {
                    this.setState({
                        time:0
                    })
                    clearInterval(this.interval);
                }
            },10)
            this.setState({status:'started'})
        }
        
    }
    
    
    render(){
       const time = new Time();
       let clock = time.getTime(this.state.time)
    return (
        <div class="calculator">
        
        {
                (this.state.status!==null) && (
                    <div className="display-countdown">
                        
                        <div className="display-countdown-time">
                            {time.getTime(this.state.time)}
                        </div>
                    </div>
                )
            }
        {this.state.status===null &&
            <div>
            <input onFocus = {() => this.onFocusChange('h')} value = {this.state.hrs} maxLength="2"
                                placeholder="00" class="calculator-screen"/>
            <input onFocus = {() => this.onFocusChange('m')} value = {this.state.min} maxLength="2"
                                placeholder="00"  class="calculator-screen"/>
            <input onFocus = {() => this.onFocusChange('s')} value = {this.state.sec} maxLength="2"
                                placeholder="00" class="calculator-screen" />

  
          <div class="calculator-keys">
          <button className = "btn btn-light btn-sm" type="button" onClick = {() =>this.InputHandler(1)}> 1 </button>
          <button className = "btn btn-light btn-sm" type="button" onClick = {() =>this.InputHandler(2)}> 2 </button>
          <button className = "btn btn btn-sm" type="button" onClick = {() =>this.InputHandler(3)}> 3 </button>
          <br/>
          <button className = "btn btn btn-sm" type="button" onClick = {() =>this.InputHandler(4)}> 4 </button>
          <button  className = "btn btn-light btn-sm" type="button" onClick = {() =>this.InputHandler(5)}> 5 </button>
          <button className = "btn btn-light btn-sm" type="button" onClick = {() =>this.InputHandler(6)}> 6 </button>
          <br/>
          <button className = "btn btn btn-sm" type="button" onClick = {() =>this.InputHandler(7)}> 7 </button>
          <button className = "btn btn-light btn-sm" type="button" onClick = {() =>this.InputHandler(8)}> 8 </button>
          <button  className = "btn btn-light btn-sm" type="button" onClick = {() =>this.InputHandler(9)}> 9 </button>
          <br />
          <button className = "btn btn-light btn-sm" type="button" onClick = {() =>this.InputHandler(0)}> 0 </button>
          <button className="btn btn-danger btn-lg btn-block-clear" onClick = {this.reset}>Reset</button>
        </div>
             
            <button  className = "btn btn-success btn-lg btn-block" onClick = {this.startHandler}>Start</button>
         
            </div>
        }
        
          
        
        {this.state.status==='started' &&
            <button className="btn btn-danger btn-lg btn-block-clear" onClick = {this.stopHandler}>Stop</button>
        }
       
      
        {this.state.status==='stopped' &&
            <button className="btn btn-success btn-lg btn-block-clear" onClick = {this.resumeHandler}>Resume</button>
        }
        
        </div>
        )
}
}

export default AdvancedTimer
