import React,{Component} from 'react';

class Welcome extends Component {
    constructor(props){
    	super(props)
    	this.state = {
    		time:new Date(),
           
            isButton :false
    	}
    }
    
    componentDidMount()
    {
        setInterval(()=>this.tick(),
      1000)
    }
    tick()
    {
        this.setState({
            time:new Date()
        })
    }
    
    buttonSet = () =>
    {
        this.setState({
            isButton:true
        })
    }
    render(){
       let date;
       if(this.state.isButton){
          date = <div>this.state.time.toLocaleString()</div>
       }
       else{
        date = <div><p>this.state.time.toLocaleTimeString()</p></div>
       }

    return (
     <>
     {date}
     <button onClick ={this.buttonSet}>Show</button>
     </>

    	)
}
}

export default Welcome
