import React,{Component} from 'react';
import Time from '../lib/Time'
import KeyPad from './KeyPad'
class AdvancedTimer extends Component {
    constructor(props){
        super(props)
        this.state = {
            time:0,
            hrs:0,
            min:0,
            sec:0,
            timeUnit:null,
            status:false
        }
    }
    componentWillUnmount() {
        clearInterval(this.interval);
    }
    onFocusChange = (Unit) =>
    {

          this.setState({
            timeUnit:Unit
          })
    }
    formatTime(time) {
        time = parseInt(time);
        return time < 10 ? '0' + time : time.toString().slice(time.toString().length - 2);
    }
    InputHandler = (time) =>
    {
        
        
        if(time&&this.state.timeUnit==='h')
        {
            if(time<0)
            {
                this.setState({
                    hrs:'00'
                })
            }
             else
                {
                    this.setState((prevState) => {
                         console.log(prevState.hrs + time)
                         let hrs = parseInt(this.formatTime(prevState.hrs + time));
                         console.log(hrs)
                         return ({ hrs: this.formatTime(hrs) });
            })

        }
        }
        else if (time&&this.state.timeUnit==='m') 
        {
            this.setState((prevState) => {
                
                let hrs = parseInt(this.formatTime(prevState.min + time));
                if(hrs>59)
                {
                    hrs = prevState.min
                }
                return ({ min: this.formatTime(hrs) });
            })
        }
        else if(time&&this.state.timeUnit==='s')
        {
            this.setState({
                sec:time,
                time:time*1000
            })
        }


    }
    startHandler = () =>
    {
        if(this.state.status!=='start'){
            this.interval = setInterval(() => 
            {
                if(this.state.time!==0&&this.state.time>0)
                {
                    this.setState((prevState) => ({time:prevState.time -10}))
                }
                else
                {
                    this.setState({
                        time:0
                    })
                    clearInterval(this.interval);
                }
            },10)
            this.setState({
                status:'start'
            });
          }
    }
    stopHandler = () => {
        console.log(this.state.time)
        if (this.state.status && this.state.status === 'start') {

            clearInterval(this.interval);
            
            this.setState((prevState) => {
                return ({
                    status: 'stopped',
                });
            });
        }
    }
    resetHandler = () =>
    {
        clearInterval(this.interval);

        this.setState(() => ({ status: null, time: 0 }));
    }
    
    
    render(){
       const time = new Time();
    return (
        <div>
        {time.getTime(this.state.time)}
        {this.state.status!=='start' &&
            <div>
            <input onFocus = {() => this.onFocusChange('h')} value = {this.state.hrs} maxLength="2"
                                placeholder="00" />
            <input onFocus = {() => this.onFocusChange('m')} value = {this.state.min} maxLength="2"
                                placeholder="00" />
            <input onFocus = {() => this.onFocusChange('s')} value = {this.state.sec} maxLength="2"
                                placeholder="00" />
             <KeyPad InputHandler={this.InputHandler} status={this.state.status} /> 
            <button className="btn btn-lg btn-success" onClick = {this.startHandler}>start</button>
            </div>
        }
        
        <button className="btn btn-lg btn-primary" onClick = {this.resetHandler}>Reset</button>
        {this.state.status==='start' &&
            <button className="btn btn-lg btn-dark" onClick = {this.stopHandler}>Stop</button>
        }
        </div>
        )
}
}

export default AdvancedTimer
