import React,{Component} from 'react';

class Message extends Component {
	constructor()
	{
		super()
		this.state = {
			message:'Visitor'
		}
	}

    ChangeMessage()
    {
        this.setState({
            message:'Madhur Sir'
        })
    }
     render(){
         return (
         	<div>
            <h1>
              Welcome {this.state.message}
            </h1>
            <button onClick ={() => this.ChangeMessage()}>Subscribe </button>
            </div>
         	)
     }

}
export default Message
