import React,{Component} from 'react';

class Form extends Component {
     constructor(props)
     {
     	super(props)
     	this.state = {
     		username :'',
     		about:''
     	}
     }
     handleUserNameChange = (event) =>{
     	this.setState({
     		username : event.target.value
     	})
     }
     handleaboutChange = (event) =>{
     	this.setState({
     		username : event.target.value
     	})
     }
     handleselectChange = (event) =>{
     	this.setState({
     		username : event.target.value
     	})
     }
     render(){
     return(
         <form>
         <div>
         <label>UserName</label>
         <input type = "text" value = {this.state.username} onChange = {this.handleUserNameChange} />
         </div>
         <div>
         <label>About</label>
         <input type = "textarea" value = {this.state.about} onChange = {this.handleaboutChange} />
         </div>
         <div>
         <label>select</label>
         <input type = "text" value = {this.state.username} onChange = {this.handleselectChange} />
         </div>
         </form>
     )}
}
export default Form
