import React,{Component} from 'react';

class Message extends Component {
	constructor(props){
        super(props)
        this.state={
          counter:this.props.number,
          date : new Date()
        }
    }
    increment(){
        this.setState({
            counter : this.state.counter + 1,
            date:new Date()
        })
    }
  
     render(){
         return (
         	<div>
            <h1>
              Welcome {this.state.counter}<br/>
              date : {this.state.date.toLocaleTimeString()}
              
            </h1>
            <button onClick ={() => this.increment()}>Subscribe </button>
            </div>
         	)
     }

}
export default Message
