import React,{Component} from 'react';

class Welcome extends Component {
    constructor(props){
    	super(props)
    	this.state = {
    		value:0
    	}
    }
    plusHandler = () =>
    {
    	this.setState({
    		value : this.state.value + 1
    	})
    }
    minusHandler = () =>
    {
    	this.setState({
    		value : this.state.value - 1
    	})
    }
    zeroHandler = () =>
    {
    	this.setState({
    		value :0
    	})
    }


    render(){
    return (
     <div>
     {this.state.value}
     <div>
     <button onClick = {this.plusHandler}>Add</button>
     </div>
     <div>
    
     <button onClick = {this.minusHandler}>Minus</button>
     </div>
     <div>
     <button onClick = {this.zeroHandler}>Refresh</button>
     </div>

     </div>

    	)
}
}

export default Welcome
